import * as attach from 'xterm/lib/addons/attach/attach'; //import SockJS from '../../public/js/sockjs.min.js'

Terminal.applyAddon(fit);
Terminal.applyAddon(attach);
export default {
  name: 'pod',
  data: function data() {
    return {
      term: null,
      websock: null,
      timer: null
    };
  },
  computed: {
    terminalId: function terminalId() {
      return "terminal_".concat(this._uid);
    },
    cluster: function cluster() {
      return this.$route.params.cluster;
    },
    namespace: function namespace() {
      return localStorage.getItem('namespace');
    },
    pod: function pod() {
      return localStorage.getItem('pod');
    },
    container: function container() {
      //return this.$route.params.container
      return '';
    },
    wsUrl: function wsUrl() {
      var protocol = window.location.protocol === 'http:' ? 'ws://' : 'wss://';
      var host = window.location.host;
      return "".concat(host); //return `${protocol}${host}`
    }
  },
  mounted: function mounted() {
    var _self = this;

    this.term = new Terminal();
    this.term.open(document.getElementById(this.terminalId));
    this.initWebSocket();
    this.term.on('data', function (data) {
      // 写给服务端, 由服务端发给container
      var msg = {
        type: "input",
        input: data
      };

      _self.websocketsend(JSON.stringify(msg));
    }), window.addEventListener("resize", function () {
      this.term.fit(); // 把web终端的尺寸term.rows和term.cols发给服务端, 通知sshd调整输出宽度

      var msg = {
        type: "resize",
        rows: this.term.rows,
        cols: this.term.cols
      };

      _self.websocketsend(JSON.stringify(msg)); // console.log(term.rows + "," + term.cols)

    });
  },
  beforeDestroy: function beforeDestroy() {
    if (this.term) {
      this.term.destroy();
    }

    window.removeEventListener('resize', this.handleResize); // 移除获取高度事件
  },
  methods: {
    initWebSocket: function initWebSocket() {
      // 初始化weosocket
      //const wsurl = `${location.origin}/api/terminal/ws`
      //const url = `${this.wsUrl}?namespace=${this.namespace}&pod=${this.pod}`
      console.log(this.wsUrl);
      var url = "ws://".concat(this.wsUrl, "/ssh?podNs=").concat(this.namespace, "&podName=").concat(this.pod, "&containerName=").concat(this.container); // https://172.18.11.25:6443/api/v1/namespaces/default/pods/nginx-deployment-5cbd8757f-d5qvx/exec?command=sh&container=nginx&stderr=true&stdin=true&stdout=true&tty=true
      //const url = `ws://${this.wsUrl}/api/terminal/ws?podNs=${this.namespace}&podName=${this.pod}` 
      //this.websock = new WebSocket(wsurl)

      this.websock = new WebSocket(url);
      this.websock.onmessage = this.websocketonmessage;
      this.websock.onopen = this.websocketonopen;
      this.websock.onerror = this.websocketonerror;
      this.websock.onclose = this.websocketclose;
    },
    websocketonopen: function websocketonopen() {// 连接建立之后执行send方法发送数据
    },
    websocketonerror: function websocketonerror() {
      // 连接建立失败重连
      this.term.write('连接出错\r\n'); // this.initWebSocket()
    },
    websocketonmessage: function websocketonmessage(e) {
      // 数据接收
      var redata = JSON.parse(e.data);
      this.term.write(redata.output);
    },
    websocketsend: function websocketsend(Data) {
      // 数据发送
      if (this.websock.readyState === 1) {
        this.websock.send(Data);
      }
    },
    websocketclose: function websocketclose(e) {
      // 关闭
      console.log('断开连接', e);
      this.socket.close();
    },
    handleResize: function handleResize() {
      var _this = this;

      if (this.timer) {
        clearTimeout(this.timer);
        this.timer = null;
      }

      this.timer = setTimeout(function () {
        _this.setTermSize();
      }, 2000);
    },
    get_term_size: function get_term_size() {
      var initWidth = 9;
      var initHeight = 17;
      var windowsWidth = document.body.clientWidth;
      var windowsHeight = document.documentElement.clientHeight;
      return {
        cols: Math.floor(windowsWidth / initWidth),
        rows: Math.floor(windowsHeight / initHeight)
      };
    },
    setTermSize: function setTermSize() {
      var _this$get_term_size = this.get_term_size(),
          cols = _this$get_term_size.cols,
          rows = _this$get_term_size.rows;

      this.term.resize(cols, rows);
      this.websock.send(JSON.stringify({
        type: "resize",
        rows: term.rows,
        cols: term.cols
      }));
    }
  }
};
