import { Terminal } from 'xterm';
import 'xterm/dist/xterm.css';
import * as fit from 'xterm/lib/addons/fit/fit';
Terminal.applyAddon(fit);
export default {
  name: 'Terminal',
  data: function data() {
    return {
      term: null,
      websock: null,
      timer: null
    };
  },
  computed: {
    terminalId: function terminalId() {
      console.log("terminal_".concat(this._uid));
      return "terminal_".concat(this._uid);
    },
    cluster: function cluster() {
      return '';
    },
    namespace: function namespace() {
      return localStorage.getItem('namespace');
    },
    pod: function pod() {
      return localStorage.getItem('name');
    },
    container: function container() {
      return '';
    },
    wsUrl: function wsUrl() {
      var host = window.location.host;
      return "".concat(host);
    }
  },
  mounted: function mounted() {
    window.addEventListener('resize', this.handleResize);
    this.initWebSocket();

    var _self = this;

    this.term = new Terminal();
    this.term.on('data', function (input) {
      var sendData = JSON.stringify({
        type: "input",
        input: input
      });

      _self.websocketsend(sendData);
    });
    this.term.open(document.getElementById(this.terminalId));
    this.term.on('resize', function () {
      //this.term.fit()
      //const re = this.get_term_size()
      var sendData = JSON.stringify({
        type: "resize",
        rows: 400,
        cols: 400
      });

      _self.websocketsend(sendData);
    });
  },
  beforeDestroy: function beforeDestroy() {
    if (this.term) {
      this.term.destroy();
    }

    window.removeEventListener('resize', this.handleResize); // 移除获取高度事件
  },
  methods: {
    initWebSocket: function initWebSocket() {
      // 初始化weosocket
      var wsuri = "ws://localhost:8000/ssh?podNs=".concat(this.namespace, "&podName=").concat(this.pod, "&containerName=").concat(this.container); //const wsuri = `ws://${this.wsUrl}/ssh?podNs=${this.namespace}&podName=${this.pod}&containerName=${this.container}`

      this.websock = new WebSocket(wsuri);
      this.websock.onmessage = this.websocketonmessage;
      this.websock.onopen = this.websocketonopen;
      this.websock.onerror = this.websocketonerror;
      this.websock.onclose = this.websocketclose;
    },
    websocketonopen: function websocketonopen() {
      // 连接建立之后执行send方法发送数据
      console.log('ws connected');
    },
    websocketonerror: function websocketonerror() {
      // 连接建立失败重连
      console.log('连接出错');
      this.term.write('连接出错\r\n'); // this.initWebSocket()
    },
    websocketonmessage: function websocketonmessage(e) {
      // 数据接收
      //const redata = JSON.parse(e.data)
      var redata = e.data;
      this.term.write(redata);
    },
    websocketsend: function websocketsend(Data) {
      // 数据发送
      if (this.websock.readyState === 1) {
        this.websock.send(Data);
      }
    },
    websocketclose: function websocketclose(e) {
      // 关闭
      console.log('断开连接', e);
      this.term.write('连接已断开\r\n');
    },
    get_term_size: function get_term_size() {
      var initWidth = 9;
      var initHeight = 17;
      var windowsWidth = document.body.clientWidth;
      var windowsHeight = document.documentElement.clientHeight;
      return {
        cols: Math.floor(windowsWidth / initWidth),
        rows: Math.floor(windowsHeight / initHeight)
      };
    },
    handleResize: function handleResize() {
      var _this = this;

      if (this.timer) {
        clearTimeout(this.timer);
        this.timer = null;
      }

      this.timer = setTimeout(function () {
        _this.setTermSize();
      }, 2000);
    },
    setTermSize: function setTermSize() {
      var _this$get_term_size = this.get_term_size(),
          cols = _this$get_term_size.cols,
          rows = _this$get_term_size.rows;

      this.term.resize(cols, rows);
      this.websock.send(JSON.stringify({
        type: "resize",
        rows: this.term.rows,
        cols: this.term.cols
      }));
    }
  }
};
