    import Dialog from '../components/Dialog';
    export default {
        name: 'fundlist',
        data(){
            return {
                search_data: {
                    startTime: '',
                    endTime: ''
                },
                paginations: {
                    page_index: 1,   //当前页数
                    total: 0,    //总条数
                    page_size: 5,   //当前页显示多少条
                    page_sizes: [5,50,100,200],   //可选每页显示多少条
                    layout: 'total,sizes,prev,next,pager,jumper'
                },
                tableData: [],
                allTableData: [],
                filterTableData: [],
                form:{
                    id:'',
                    hostname:'',
                    title:'',
                    note:'',
                    opreater:'',
                    updatetime:''

                },
                dialog: {
                    show: false,
                    title:'',
                    option:'edit'
                }
            }
        },
/*        computed:  {
            user(){
                return this.$store.getters.user;
            }
        },*/
        created(){
            this.getProfile();
        },
        methods: {
            getProfile(){
                //获取后端数据
                this.$axios.get("/api/crontab/list")
                    .then(res => {
                        this.allTableData = res.data.data;
                        this.filterTableData = res.data.data;
                        //设置分页数据
                        this.setPaginations();
                    });
            },
            setPaginations(){
                //分页属性设置
                this.paginations.total = this.allTableData.length;
                this.paginations.page_index = 1;
                this.paginations.page_size = 5;
                //设置默认的分页数据
                this.tableData = this.allTableData.filter((item, index) => {
                    return index < this.paginations.page_size;
                });
            },
            handleEdit(index,row){
                this.dialog = {
                    show:true,
                    title:'修改任务信息',
                    option:'edit'
                };
                this.form = {
                    hostname: row.hostname,
                    title: row.title,
                    note: row.note,
                    opreater: row.opreater,
                    updatetime:row.updatetime,
                    id: row.id
                }
            },
            handleDelete(index,row){
                let data = row.id;
                const params = new URLSearchParams();
                params.append('id', data);
                this.$axios.post('/api/crontab/delete',params,{headers: {'Content-Type':'application/x-www-form-urlencoded'}})
                    .then(res => {
                        this.$message('删除成功');
                        this.getProfile();
                    });
            },
            handleAdd(index,row){
                this.dialog = {
                    show:true,
                    title:'添加任务信息',
                    option:'add'
                };
                this.form = {
                    hostname: '',
                    title: '',
                    note: '',
                    opreater: '',
                    updatetime:'',
                    id: '0'
                };
                this.dialog.show = true
            },
            handleSizeChange(page_size){
                //切换size
                this.paginations.page_index = 1;
                this.paginations.page_size = page_size;
                console.log(this.allTableData);
                this.tableData = this.allTableData.filter((item, index) => {
                    return index < page_size;
                });
            },
            handleCurrentChange(page){
                //获取当前页
                let index = this.paginations.page_size * (page -1);
                //数据总数
                let nums = this.paginations.page_size * page;
                let tables = [];
                for (let i=index;i<nums;i++){
                    if (this.allTableData[i]) {
                        tables.push(this.allTableData[i]);
                    }
                    this.tableData = tables;
                }
            },
            handleSearch(){
                //筛选
                if(!this.search_data.startTime|| !this.search_data.endTime) {
                    this.$message({
                        type:'warning',
                        message:'请选择时间区间'

                    });
                    this.getProfile();
                    return;
                }
                const sTime = this.search_data.startTime.getTime();
                const eTime = this.search_data.endTime.getTime();
                this.allTableData = this.filterTableData.filter(item => {
                    let date = new Date(item.updatetime);
                    let time = date.getTime();
                    return time >= sTime && time <= eTime;
                });
                //重新调用分页方法
                this.setPaginations();
            }
        },
        components: {  //注册dialog
            Dialog
        }
    };
